import React, {Component} from 'react';
import './App.css';
import {Container, Nav} from "./components/styled-components";
import "bootstrap/dist/css/bootstrap.css";
import {Bar} from 'react-chartjs-2';
import axios from 'axios';

class App extends Component {
  constructor(props){
    super(props);

    this.onChangeCity = this.onChangeCity.bind(this);
    this.onChangeCountry = this.onChangeCountry.bind(this);
    this.onChangeProvince = this.onChangeProvince.bind(this);
    this.onSubmit = this.onSubmit.bind(this);

    this.state = {
        Country: "United States of America",
        Province:  "North Carolina",
        City: "Mecklenburg",
        queriedData: [{Country: "Loading...", Province: "Loading...", City: "Loading...", Deaths: 0, Date:"Loading...", Active:0, Confirmed: 0}],
        graphData:{},
        lastMax: {
            Deaths: 0,
            Active: 0,
            Confirmed: 0,
            Recovered: 0
        }
    }
  }
  queryData(){
        axios.get('http://localhost:5000/main/querySimpleData', {
            params:{
                Country: this.state.Country,
                City:this.state.City,
                Province:this.state.Province
            }
        })
            .then(response =>{
                if(response.data.length){
                    this.setState({
                        lastMax:{
                            Deaths: this.state.queriedData[this.state.queriedData.length - 1]["Deaths"],
                            Active: this.state.queriedData[this.state.queriedData.length - 1]["Active"],
                            Confirmed: this.state.queriedData[this.state.queriedData.length - 1]["Confirmed"],
                            Recovered: this.state.queriedData[this.state.queriedData.length - 1]["Confirmed"]-this.state.queriedData[this.state.queriedData.length - 1]["Active"]-this.state.queriedData[this.state.queriedData.length - 1]["Deaths"]
                        }
                    })

                    this.setState({queriedData: response.data})
                    console.log(this.state.lastMax)
                    console.log(response.data)
                    this.updateSimpleQuery()
                }else{
                    alert("Invalid Query, try to enter different parameters")
                }
            })
            .catch((error)=>{
                console.log(error)
            })
  }
  componentDidMount(){
      this.queryData();

  }
  onChangeCountry(e){
      this.setState({
          Country: e.target.value
      });
  }
  onChangeCity(e){
      this.setState({
          City: e.target.value
      });
  }
  onChangeProvince(e){
      this.setState({
          Province: e.target.value
      });
  }
  onSubmit(e){
      e.preventDefault();
      this.queryData();

  }
  render(){
  return (
    <Container>
      <Nav className = "navbar navbar-expand-lg main-nav fixed-top">
        <Container className = "navbar-brand mb-0">
          Coronavirus Dashboard
        </Container>
        <Container className = "navbar-nav ml-auto">
          <Container>
            Created and Maintained by Hari Shankar
          </Container>
        </Container>
      </Nav>


      <Container className = "container-fluid pr-5 pl-5 pt-5 pb-5">
        <Container className = "row">
          <Container className = "col-lg-4 mb-4">
            <Container className = "card query-card">
              <div className = "form">
                <input type = "text" name = "Country" autoComplete = "off" required = {true}/>
                <label htmlFor = "Country" className = "label-name">
                  <span className = "content-name">Country</span>
                </label>
              </div>
            </Container>
          </Container>
          <Container className = "col-lg-4 mb-4">
            <Container className = "card query-card">
              <div className = "form">
                <input type = "text" name = "Province" autoComplete = "off" required = {true}/>
                <label htmlFor = "Province" className = "label-name">
                  <span className = "content-name">Province</span>
                </label>
              </div>
            </Container>
          </Container>
          <Container className = "col-lg-4 mb-4">
          <Container className = "card query-card">
            <div className = "form">
              <input type = "text" name = "City" autoComplete = "off" required = {true}/>
              <label htmlFor = "City" className = "label-name">
                <span className = "content-name">City</span>
              </label>
            </div>
          </Container>
          </Container>
        </Container>
        <Container className = "row mb-4">
          <Container className = "col-lg-3">
            <Container className="card grid-card">
              <Container className = "card-heading">
                <Container>
                  Total Deaths
                </Container>

              </Container>
              <Container className="card-value pt-1 number-text">
                7329148
              </Container>
            </Container>
          </Container>
          <Container className = "col-lg-3">
            <Container className="card grid-card">
              <Container className = "card-heading">
                <Container>
                  Total Confirmed Cases
                </Container>

              </Container>
              <Container className="card-value pt-1 number-text">
                9382470
              </Container>
            </Container>
          </Container>
          <Container className = "col-lg-3">
            <Container className="card grid-card">
              <Container className = "card-heading">
                <Container>
                  Total Active Cases
                </Container>

              </Container>
              <Container className="card-value pt-1 number-text">
                9382470
              </Container>
            </Container>
          </Container>
        </Container>
        <Container className = "row">
          <Container className = "col-lg-9">
            <Container className = "card grid-card">

            </Container>
          </Container>
        </Container>
      </Container>
    </Container>
  );
  }
}

export default App;
